<div class="chart-update-buttons" style="margin-left: 24px;">
{{--    <button class="btn btn-primary btn-simple btn-sm" data-time-period="day" data-chart-id="market_cap">1d</button>--}}
{{--    <button class="btn btn-secondary btn-simple btn-sm" data-time-period="week" data-chart-id="market_cap">7d</button>--}}
    <button class="btn btn-primary btn-simple btn-sm" data-time-period="month" data-chart-id="market_cap">1m</button>
{{--    <button class="btn btn-secondary btn-simple btn-sm">3m</button>--}}
{{--    <button class="btn btn-secondary btn-simple btn-sm" data-time-period="year" data-chart-id="market_cap">1y</button>--}}
{{--    <button class="btn btn-secondary btn-simple btn-sm">YTD</button>--}}
    <button class="btn btn-secondary btn-simple btn-sm" data-time-period="all" data-chart-id="market_cap">All</button>
</div>
<canvas class="chartjs-render-monitor lineGraph"
        data-chart-title="{{ __('pegnet.total_market_cap') }}"
        data-chart-id="market_cap"
        data-chart-series="USD"
        data-chart-point-interval="86400"
        data-chart-data='@json($graphData)'>
</canvas>

